def rotateMatrix90Clockwise(matrix):
    # Initialize a new matrix to hold the rotated values
    newMatrix = [];

    # Loop through each row of the matrix
    for i in range(len(matrix)):
        # Get the current row of the matrix
        row = matrix[i];

        # Loop through each column of the row
        for j in range(len(row)):
            # Get the current element of the row
            element = row[j];

            # Rotate the element 90 degrees clockwise
            newElement = (element[0], element[1] - element[0][0]);

            # Add the rotated element to the new matrix
            newMatrix.append(newElement);
    return newMatrix;

