    for i in range(n1+1): 
        for j in range(n2+1): 
            if (i == 0 or j == 0): 
                LCSuff[i][j] = 0
            elif (str1[i-1] == str2[j-1]): 
                LCSuff[i][j] = LCSuff[i-1][j-1] + 1
                if (lenght < LCSuff[i][j]): 
                    lenght = LCSuff[i][j] 
                    res = str1[i-lenght: i] 
            else: 
                LCSuff[i][j] = 0

